var players = JSON.parse(data);

var heat = [];
var spurs = [];

function heatOrSpurs(players){

	for(var i = 0; i < players.length; i++){
		if(players[i].team === "Heat"){
			heat.push(players[i]);
		} else{
			spurs.push(players[i]);
		}
	}
	return heat;
	return spurs;
}

heatOrSpurs(players);

function calculateScore(team){
	var score = 0;
	for(var i = 0; i < team.length; i++){
		var threes = team[i].threesMade * 3;
		var twos = (team[i].fieldGoalsMade - team[i].threesMade) * 2;
		var ones = team[i].freeThrowsMade;

		score = score + threes + twos + ones;
	}

	return score;
}

function percentageFromThrees(players){
	var highestPercentage = 0;
	var highestPlayer = "";
	for(var i = 0; i < players.length; i++){
		var player = players[i].name;
		/*var threes = players[i].threesMade * 3;
		var twos = (players[i].fieldGoalsMade - players[i].threesMade) * 2;
		var ones = players[i].freeThrowsMade;*/
		var totalPoints = (players[i].threesMade * 3) + ((players[i].fieldGoalsMade - players[i].threesMade) * 2) + (players[i].freeThrowsMade);
		var threePercent = (Math.floor(((players[i].threesMade * 3)/totalPoints) * 100));

		if (totalPoints > 10 && threePercent > highestPercentage){
			highestPercentage = threePercent;
			highestPlayer = player;
		}
	}
	return highestPlayer;
}



function mostRebounds(team1, team2){
	var team1Name = team1[0].team;
	var team1Rebounds = 0;

	var team2Name = team2[0].team;
	var team2Rebounds = 0;

	for(var i = 0; i < team1.length; i++){
		team1Rebounds = team1Rebounds + team1[i].defensiveRebounds + team1[i].offensiveRebounds;
	}

	for(var i = 0; i < team1.length; i++){
		team2Rebounds = team2Rebounds + team2[i].defensiveRebounds + team2[i].offensiveRebounds;
	}

	if(team1Rebounds > team2Rebounds){
		return team1Name + " with " + team1Rebounds;
	}else{
		return team2Name + " with " + team2Rebounds;
	}
}

function mostAssists(players){
	var highestAssists = 0;
	var highestPlayer = "";
	for(var i = 0; i < players.length; i++){
		if(players[i].position != "G"){
			var player = players[i].name;
			var assists = players[i].assists;

			if(assists > highestAssists){
				highestAssists = assists;
				highestPlayer = player;
			}
		}
	}
	return highestPlayer + " with " + highestAssists;
}

function turnoversVAssusts(players){
	var moreTurnovers = [];
	for(var i = 0; i < players.length; i++){
		if(players[i].turnovers > players[i].assists){
			moreTurnovers.push(players[i].name);
		}
	}
	return moreTurnovers;
}





console.log("Final Score: Heat: " + calculateScore(heat) + ", Spurs: " + calculateScore(spurs) + "\n =====");
console.log("* Player with highest percentage of points from three pointers: " + percentageFromThrees(players));
console.log("* Team with most rebounds: " + mostRebounds(heat, spurs));
console.log("* Non guard player with most assists: " + mostAssists(players));
console.log("* Players with more turnovers than assists: \n" + turnoversVAssusts(players));











